input_type.script:
#!/bin/bash
OracleJava8JDKVersion=$(ls /Library/Java/JavaVirtualMachines | grep "jdk" | grep "1.8" | sed 's/.jdk//' | sed 's/jdk//' | sed -n '$p')
if [ "$OracleJava8JDKVersion" != "" ]; then echo "<result>$OracleJava8JDKVersion</result>" else echo "<result>JDKNotInstalled</result>" fi exit 0
